import React, { useState } from 'react';
import './Expense.css';
import logo from '../img/logo3.png';
import des3 from '../img/des3.png';
import dollar from '../img/dollar-sign.jpeg';
import { v4 as uuidv4 } from 'uuid';

function Expense(props) {
    // useState()
    const [title, setTitle] = useState(''); // State เพื่อเก็บค่าของ input สำหรับ description
    const [amount, setAmount] = useState(''); // State เพื่อเก็บค่าของ input สำหรับจำนวนเงิน
    const [selectedOption, setSelectedOption] = useState('');
    //const [invalid , setInvalid] = useState(false); //disabled={!invalid}
    // function to deal with input from description
    function inputTitle(event) {
        setTitle(event.target.value); // เก็บค่าที่ป้อนเข้ามาใน state สำหรับ description
    }

    // function to deal with amount input
    function inputAmount(event) {
        const value = event.target.value;

        // ตรวจสอบว่าเป็นตัวเลขที่มากกว่าหรือเท่ากับ 0 หรือไม่
        if (isNaN(value) || value < 0) {
            alert('Please enter a valid positive number');
        } else {
            setAmount(value); // เก็บค่าที่ถูกต้องลงใน state สำหรับจำนวนเงิน
        }
    }

    // function for options before submitting
    function options(event) {
        setSelectedOption(event.target.value);
    }

      // function to handle confirm button
    function handleConfirm() {
        if (title.trim() && amount && selectedOption !== '') {
            const expenseData = {
                id: uuidv4(), // เพิ่ม id เพื่อใช้เป็น key ใน Transaction
                description: title, // title from useState
                amount: Number(amount), // amount from useSate
                option: selectedOption // option from options button
            };
            props.onConfirm(expenseData); // ส่งข้อมูลไปยัง App component
            setTitle('');
            setAmount('');
        }
    }
    // function to handle cancel button
    function handleCancel() {
        // set the value of title and amount back to ''
        setTitle('');
        setAmount('');
    }


    return (
        <div className="container">
            <img src={logo} id="main-logo" alt="logo" />
            <div className="addExpense">
                <section className="description">
                    <img src={des3} id="img-description" alt="description" />
                    <input
                        type="text"
                        placeholder="Enter a description"
                        value={title} // set value back to the default
                        onChange={inputTitle}
                    />
                </section>
                <section className="amount">
                    <img src={dollar} id="img-dollar" alt="dollar" />
                    <input
                        type="number"
                        placeholder="0.00"
                        value={amount}  // set value back to the default
                        onChange={inputAmount}
                    />
                </section>
            </div>
            <div className="menu-container">
                <div className="dropdown">
                    <select className="dropdown-content" onChange={options}>
                        <option value="">Click to see options</option>
                        <option value="iPaidSplit">Paid by you, split equally</option>
                        <option value="youPayMe">You are owed the full amount</option>
                        <option value="youPaidSplit">Your partner paid, split equally</option>
                        <option value="iPayBack">Your partner is owed the full amount</option>
                        <option value="moreOptions">More options</option>
                    </select>                
                </div>
                <button id="confirmButton" onClick={handleConfirm}>Confirm</button>
                <button id="cancelButton" onClick={handleCancel}>Cancel</button>
            </div>
        </div>
    );
}

export default Expense;
