import React, { useState } from 'react';
import './App.css';
import Expense from './components/Expense';
import Transaction from './components/Transaction';
import DataContext from './data/DataContext';
import Signup from './components/Signup';
import Login from './components/Login';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

function App() {
  const [expenseData, setExpenseData] = useState([]);
  const [lent, setLent] = useState(0);
  const [borrowed, setBorrowed] = useState(0);
  const [isSignedUp, setIsSignedUp] = useState(false);

  function handleExpenseData(data) {
    setExpenseData([data, ...expenseData]);
  }

  function handleSignUp() {
    setIsSignedUp(true);
  }

  return (
    <Router>
      <DataContext.Provider value={{ lent, borrowed, setLent, setBorrowed }}>
        <div>
          <Routes>
            <Route
              path="/expense"
              element={
                <Expense onConfirm={handleExpenseData}>
                  <Route path="/transaction" element={<Transaction expenseData={expenseData} />} />
                </Expense>
              }
            />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup onSignUp={handleSignUp} />} />
          </Routes>
        </div>
      </DataContext.Provider>
    </Router>
  );
}

export default App;
